const PrettoSlider = withStyles({
  root: {
      color: '#0041C2',
      height: 5,
  },
  thumb: {
      height: 20,
      width: 20,
      backgroundColor: '#0041C2',
      border: '2px solid currentColor',
      marginTop: -8,
      marginLeft: -5,
      '&:focus,&:hover,&$active': {
          boxShadow: 'inherit',
      },
  },
  active: {},
  valueLabel: {
      left: 'calc(-50% + 4px)',
  },
  track: {
      height: 5,
      borderRadius: 4,
  },
  rail: {
      height: 5,
      borderRadius: 4,
  },
})(Slider);

var tempSlider;

class modal extends Component {
  constructor(props){
    super(props)
    this.state={
      isOn: "off",
      tempvalue:20,
    };
    this.onSubmit=this.onSubmit.bind(this)
    this.setTemp=this.setTemp.bind(this)
    this.setOn=this.setOn.bind(this)
  }

  setTemp=(event,value)=>{this.setState({tempvalue:value})}


  setOn = (event) => {
        this.setState({ isOn: event.target.value });
    }

    onSubmit(e)
    {

        if(this.state.isOn=="on"){
        let info1={
            no:this.state.ac
        }
        console.log(info1)
        axios.post('http://localhost:5000/control/on',info1)
        .then(res=>
            {
                console.log(this.state.tempvalue)

            })

        let info2={
            temp:this.state.tempvalue,
            no:this.state.ac
        }
        console.log(info2)
        axios.post('http://localhost:5000/control/change',info2)
        .then(res=>
            console.log(''))

        }
        else if(this.state.isOn=="off"){
            let info1={
                no:this.state.ac
            }
        console.log(info1)

            axios.post('http://localhost:5000/control/off',info1)
                .then(res=>
                    console.log('hello')
                    )
            }

    }

  render(props){
    if (this.state.isOn == "on") {

      tempSlider = 
      <div>
          <InputLabel>Temperature</InputLabel>
          <PrettoSlider valueLabelDisplay="auto" aria-label="pretto slider" defaultValue={20} min={16} max={30} onChange={this.setTemp} />
      </div>
  } else {
      tempSlider = <></>
  }
    return (
      <div>
        <div className="modal-wrapper" style={{transform: props.show ? 'translateY(0vh)' : 'translateY(-100vh)',opacity: props.show ? '1' : '0'}}>
            <Modal.Dialog>
            <Modal.Header closeButton>
              <Modal.Title>Control AC</Modal.Title>
            </Modal.Header>

            <Modal.Body>
              <form class="form">     
                    <br></br>
                        <InputLabel>On/Off</InputLabel>
                        <Select onChange={this.setOn} value={this.state.isOn}>
                            <MenuItem value="off">Off</MenuItem>
                            <MenuItem value="on">On</MenuItem>
                        </Select>


                        {tempSlider}

                        <Button variant="outlined" color="primary" onClick={this.onSubmit}>Set</Button>
              </form>
            </Modal.Body>

            <Modal.Footer>
              <Button variant="outlined" color="primary" onClick={props.close}>Close</Button>

            </Modal.Footer>
            </Modal.Dialog>
        </div>
      </div>
    )

  }
}
export default modal;
