import React, { Component } from 'react';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import Select from '@material-ui/core/Select';
import Button from '@material-ui/core/Button';
import Slider from '@material-ui/core/Slider';
import { withStyles, makeStyles } from '@material-ui/core/styles';
import './control.css';
import axios from 'axios'
import PinchZoomPan from 'react-responsive-pinch-zoom-pan';
import jwt_decode from 'jwt-decode'
import { decode } from 'punycode';

const PrettoSlider = withStyles({
    root: {
        color: '#0041C2',
        height: 5,
    },
    thumb: {
        height: 20,
        width: 20,
        backgroundColor: '#0041C2',
        border: '2px solid currentColor',
        marginTop: -8,
        marginLeft: -5,
        '&:focus,&:hover,&$active': {
            boxShadow: 'inherit',
        },
    },
    active: {},
    valueLabel: {
        left: 'calc(-50% + 4px)',
    },
    track: {
        height: 5,
        borderRadius: 4,
    },
    rail: {
        height: 5,
        borderRadius: 4,
    },
})(Slider);

var tempSlider;
var imageview;

class Control extends Component {
    constructor(props) {
        super(props);

        this.state = {
            first_name: '',
            last_name: '',
            email: '',
            position: '',
            floor: '',
            lab: '',
            isOn: "off",
            tempvalue:20,
            ac:-1,
            select:0,
            print:''
        };
        this.onSubmit=this.onSubmit.bind(this)
        this.setTemp=this.setTemp.bind(this)
        this.ac=this.ac.bind(this)
        this.wing=this.wing.bind(this)

    }

    setTemp=(event,value)=>{this.setState({tempvalue:value})}


    setOn = (event) => {
        this.setState({ isOn: event.target.value });
    }

    componentDidMount() {
        const token = localStorage.usertoken
        const decoded = jwt_decode(token)
        console.log(decoded)
        this.setState({
          first_name: decoded.first_name,
          last_name: decoded.last_name,
          email: decoded.email,
          position: decoded.position,
          floor: decoded.floor,
          lab: decoded.lab
        })
      }

    ac(e){
        this.setState({ac:e})
    }

    wing(e){


        this.setState({select:e})


    }
    onSubmit(e)
    {

    if(this.state.isOn=="on"){
    let info1={
        no:this.state.ac
    }
    console.log(info1)
    axios.post('http://localhost:5000/control/on',info1)
     .then(res=>
        {
            console.log(this.state.tempvalue)

        })

    let info2={
        temp:this.state.tempvalue,
        no:this.state.ac
    }
    console.log(info2)
    axios.post('http://localhost:5000/control/change',info2)
    .then(res=>
        console.log(''))

    }
    else if(this.state.isOn=="off"){
        let info1={
            no:this.state.ac
        }
    console.log(info1)

        axios.post('http://localhost:5000/control/off',info1)
            .then(res=>
                console.log('hello')
                )
        }

    }


    render() {

        if(this.state.floor=='3')
        {

            if(this.state.select==0 )
            {

                imageview=
                <>
                <img src={require(('./third/3.jpg'))} alt="resposnive imgage with clickable areas" width="800" height="500" className="aligncenter size-full wp-image-3344" useMap="#image-map" />
                <map name="image-map">
                    <area  alt="WING-IV" title="WING-IV"  coords="15,17,285,195" shape="rect" onClick={()=>this.wing(4)}/>
                    <area  alt="WING-III" title="WING-III"  coords="344,13,551,169" shape="rect" onClick={()=>this.wing(4)}/>
                    <area  alt="WING-II" title="WING-II" coords="345,224,550,398" shape="rect" onClick={()=>this.wing(4)}/>
                    <area  alt="WING-I" title="WING-I"  coords="14,226,268,406" shape="rect" onClick={()=>this.wing(4)}/>
                </map>
                </>

            }
            else if(this.state.select==1)
            {

                imageview=
                <img src={require(('./third/wing-1.jpg'))} alt="resposnive imgage with clickable areas" width="800" height="500" className="aligncenter size-full wp-image-3344" useMap="#image-map" />
            }
            else if(this.state.select==2)
            {
                imageview=
                <img src={require(('./third/wing-2.jpg'))} alt="resposnive imgage with clickable areas" width="800" height="500" className="aligncenter size-full wp-image-3344" useMap="#image-map" />
            }
            else if(this.state.select==3)
            {
                imageview=
                <img src={require(('./third/wing-3.jpg'))} alt="resposnive imgage with clickable areas" width="800" height="500" className="aligncenter size-full wp-image-3344" useMap="#image-map" />
            }
            else if(this.state.select==4)
            {
                imageview=
                <img src={require(('./third/wing-4.jpg'))} alt="resposnive imgage with clickable areas" width="600" height="420" className="aligncenter size-full wp-image-3344" useMap="#image-map" />
            }

        }
        // this.setState({print:imageview})
        if (this.state.isOn == "on") {

            tempSlider = 
            <div>
                <InputLabel>Temperature</InputLabel>
                <PrettoSlider valueLabelDisplay="auto" aria-label="pretto slider" defaultValue={20} min={16} max={30} onChange={this.setTemp} />
            </div>
        } else {
            tempSlider = <></>
        }

        return (
            <div>
                <h2 class="h2">Control AC</h2>
                <br></br>
                <form class="form">




                    {imageview}







                    <br></br><br></br>

                    <InputLabel>On/Off</InputLabel>
                    <Select onChange={this.setOn} value={this.state.isOn}>
                        <MenuItem value="off">Off</MenuItem>
                        <MenuItem value="on">On</MenuItem>
                    </Select>
                    <br></br><br></br>

                    {tempSlider}

                    <Button variant="outlined" color="primary" onClick={this.onSubmit}>Set</Button>
                </form>
            </div>
        );
    }
}

export default Control;
