      left: 'calc(-50% + 4px)',
  },
  track: {
      height: 5,
      borderRadius: 4,
  },
  rail: {
      height: 5,
      borderRadius: 4,
  },
})(Slider);

var tempSlider;
var isOn
var tempvalue

const modal = (props) => {
    return (
      <div>
        <div className="modal-wrapper"
        style={{
          transform: props.show ? 'translateY(0vh)' : 'translateY(-100vh)',
          opacity: props.show ? '1' : '0'
      }}>
