const PrettoSlider = withStyles({
  root: {
      color: '#0041C2',
      height: 5,
  },
  thumb: {
      height: 20,
      width: 20,
      backgroundColor: '#0041C2',
      border: '2px solid currentColor',
      marginTop: -8,
      marginLeft: -5,
      '&:focus,&:hover,&$active': {
          boxShadow: 'inherit',
      },
  },
  active: {},
  valueLabel: {
      left: 'calc(-50% + 4px)',
  },
  track: {
      height: 5,
      borderRadius: 4,
  },
  rail: {
      height: 5,
      borderRadius: 4,
  },
})(Slider);

var tempSlider;

class modal extends Component {
  constructor(props){
    super(props)
    this.state={
      isOn: "off",
      tempvalue:20,
      prop:props
    };
  }
  render(){
    if (this.state.isOn == "on") {

      tempSlider = 
      <div>
          <InputLabel>Temperature</InputLabel>
          <PrettoSlider valueLabelDisplay="auto" aria-label="pretto slider" defaultValue={20} min={16} max={30} onChange={this.setTemp} />
      </div>
  } else {
      tempSlider = <><br/><br/><br/></>
  }
    return (
      <div>
        <div className="modal-wrapper" style={{transform: props.show ? 'translateY(0vh)' : 'translateY(-100vh)',opacity: props.show ? '1' : '0'}}>
            <Modal.Dialog>
            <Modal.Header closeButton>
              <Modal.Title>Control AC</Modal.Title>
            </Modal.Header>

            <Modal.Body>
              <form class="form">     
                    <br></br>
                        <InputLabel>On/Off</InputLabel>
                        <Select onChange={this.setOn} value={this.state.isOn}>
                            <MenuItem value="off">Off</MenuItem>
                            <MenuItem value="on">On</MenuItem>
                        </Select>


                        {tempSlider}

                        <Button variant="outlined" color="primary" onClick={this.onSubmit}>Set</Button>
              </form>
            </Modal.Body>

            <Modal.Footer>
              <Button variant="secondary" onClick={props.close}>Close</Button>

            </Modal.Footer>
           </Modal.Dialog>
        </div>
      </div>
    )

  }
}
export default modal;
